import React from 'react';

export default class Application extends React.Component {
    constructor() {
        super();
        this.state = {
            title: '',
            description: '',
            importance: 'high',
            items: []
        }
    };


    myFormSubmit = (e) => {
        e.preventDefault();

        let items = [...this.state.items];
        items.push({
            title: this.state.title,
            description: this.state.description,
            importance: this.state.importance
        });

        this.setState({
            items,
            title: '',
            description: '',
            importance: 'high'
        });
    };

    myHandleChange = (e) => {
        let name = e.target.name;
        let value = e.target.value;
        this.setState({
            [name]: value
        });
        //console.log(name);
        //console.log(value);
    }
    
    myHandleDelete = (i) => {
        this.setState ({
            items: this.state.items.splice(i, 1)
        });
    }
    
    render() {
        return(
            <div>
                <Form myFormSubmit={this.myFormSubmit} myHandleChange={this.myHandleChange} newTitle={this.state.title} newDescription={this.state.description} newImportance={this.state.importance}/>
                <Table items={this.state.items} myHandleDelete={this.myHandleDelete}/>
            </div>
        )
    }

}
class Table extends React.Component {
    render() {
        const items = this.props.items;
        return (
            <div id="table" className="col-sm-5 float-right">
                <table className="table table-bordered">
                    <tbody>
                        <tr>
                            <th>Title</th>
                            <th>Description</th>
                            <th>Importance</th>
                            <th>Action</th>
                        </tr>
                        {items.map((item,i) => {
                            return (
                                <tr key={i}>
                                    <td>{item.title}</td>
                                    <td>{item.description}</td>
                                    <td>{item.importance}</td>
                                    <td><button onClick={this.props.myHandleDelete.bind(this)}>Delete</button></td>
                                </tr>
                            );
                        })}
                    </tbody>
                </table>
            </div>
        )
    }
}

class Form extends React.Component {
    render() {
        return (
            <div id="form" className="col-sm-5 float-left">
                <form onSubmit={this.props.myFormSubmit} >
                    <div>
                        <label htmlFor="title">Title</label>
                        <input type="text" name="title" id="title" value={this.props.newTitle} onChange={this.props.myHandleChange}/>
                    </div>
                    <div>
                        <label htmlFor="description">Description</label>
                        <textarea type="text" name="description" id="description" value={this.props.newDescription} onChange={this.props.myHandleChange}></textarea>
                    </div>
                    <div>
                        <label htmlFor="importance">Importance</label>
                        <select name="importance" id="importance" value={this.props.newImportance} onChange={this.props.myHandleChange}>
                            <option value="high">high</option>
                            <option value="medium">medium</option>
                            <option value="low">low</option>
                        </select>
                    </div>
                    <button type="submit" value="submit" className="btn-primary">Submit</button>
                </form>
            </div>
        )
    }
}
