import React, { Component } from 'react'

class LoginForm extends Component {
  constructor(props) {
    super(props)
  
    this.state = {
      name: '',
      password: '',
      isLogin: false,
      users: {   
        username:"hruday@gmail.com",
        password :'hruday123' 
      } 
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit= this.handleSubmit.bind(this);
  }

  handleSubmit = (e) => {
    const {name, password} = this.state;
    e.preventDefault();
    alert(name);
  }

  handleChange = (e) => {
    this.setState ({
      [e.target.name]: e.target.value
    })
  }
  
  render() {
    const {username, password, isLogin} = this.state
    return (
      <form onSubmit={this.handleSubmit}>
        <label htmlFor="username">Username</label>
        <input type="text" name="username" value={username} id="username" onChange={this.handleChange}/>
        <label htmlFor="password">Password</label>
        <input type="password" name="password" value={password} id="password" onChange={this.handleChange}/>
        <button type="submit" disabled={isLogin}>Login</button>
      </form>
    )
  }
}

export default LoginForm
