import React from 'react';

export default class Application extends React.Component {
    constructor() {
        super();
        this.state = {
            title: '',
            description: '',
            importance: '',
            items: []
        }
    }
    
    myHandleChange = (e) => {
        let name = e.target.name;
        let value = e.target.value;
        this.setState({
            [name]: value
        });
    }
    
    render() {
        return(
            <div>
                <Form myHandleChange={this.myHandleChange}/>
            </div>
        )
    }

}
