class Form extends React.Component {
    render() {
        return (
            <div id="form">
                <form>
                    <div>
                        <label htmlFor="title">Title</label>
                        <input type="text" name="title" id="title" onChange={this.props.myHandleChange}/>
                    </div>
                    <div>
                        <label htmlFor="description">Description</label>
                        <textarea type="text" name="description" id="description" onChange={this.props.myHandleChange}></textarea>
                    </div>
                    <div>
                        <label htmlFor="importance">Importance</label>
                        <select name="importance" id="importance" onChange={this.props.myHandleChange}>
                            <option value="high">High</option>
                            <option value="high">Medium</option>
                            <option value="high">Low</option>
                        </select>
                    </div>
                    <button type="submit" value="submit">Submit</button>
                </form>
            </div>
        )
    }
}
