import React from 'react';

export default class Application extends React.Component {
    constructor() {
        super();
        this.state = {
            title: '',
            description: '',
            importance: '',
            items: []
        }
    }
    
    myFormSubmit = (e) => {
        e.preventDefault();

        let items = [...this.state.items];
        items.push({
            title: this.state.title,
            description: this.state.description,
            importance: this.state.importance
        });

        this.setState({
            items,
            title: '',
            description: '',
            importance: ''
        });
    }

    myHandleChange = (e) => {
        let name = e.target.name;
        let value = e.target.value;
        this.setState({
            [name]: value
        });
        console.log(name);
        console.log(value);
    }
    
    render() {
        return(
            <div>
                <Form myFormSubmit={this.myFormSubmit} myHandleChange={this.myHandleChange} newTitle={this.state.title} newDescription={this.state.description} newImportance={this.state.importance}/>
                <Table/>
            </div>
        )
    }

}
class Table extends React.Component {
    render() {
        return (
            <div id="table">
                <table>
                    <tbody>
                        <tr>
                            <th>Title</th>
                            <th>Description</th>
                            <th>Importance</th>
                            <th>Action</th>
                        </tr>
                    </tbody>
                </table>
            </div>
        )
    }
}

class Form extends React.Component {
    render() {
        return (
            <div id="form">
                <form>
                    <div>
                        <label htmlFor="title">Title</label>
                        <input type="text" name="title" id="title" value={this.state.newTitle} onChange={this.props.myHandleChange}/>
                    </div>
                    <div>
                        <label htmlFor="description">Description</label>
                        <textarea type="text" name="description" id="description" value={this.state.newDescription} onChange={this.props.myHandleChange}></textarea>
                    </div>
                    <div>
                        <label htmlFor="importance">Importance</label>
                        <select name="importance" id="importance" value={this.state.newImportance} onChange={this.props.myHandleChange}>
                            <option value="high">high</option>
                            <option value="medium">medium</option>
                            <option value="low">low</option>
                        </select>
                    </div>
                    <button type="submit" value="submit">Submit</button>
                </form>
            </div>
        )
    }
}
